    getContentPane ().add ("West", toolPanel);
    getContentPane ().add ("Center", (Component) view ());
    JPanel buttonPalette = createButtonPanel ();
    createButtons (buttonPalette);
    getContentPane ().add ("South", buttonPalette);
    initDrawing ();
    setupAttributes ();
}

