public Iconkit (Component component) {
    fMap = new Hashtable (53);
    fRegisteredImages = CollectionsFactory.current ().createList (10);
    fComponent = component;
    fgIconkit = this;
}

public static Iconkit instance () {
    return fgIconkit;
