public Animator (Animatable animatable, DrawingView view) {
    super ("Animator");
    fView = view;
    fAnimatable = animatable;
}

public void start () {
    super.start ();
    fIsRunning = true;
}

public void end () {
    fIsRunning = false;
}

public void run () {
    while (fIsRunning) {
        long tm = System.currentTimeMillis ();
        fView.freezeView ();
        fAnimatable.animationStep ();
        fView.checkDamage ();
        fView.unfreezeView ();
        try {
            tm += DELAY;
            Thread.sleep (Math.max (0, tm - System.currentTimeMillis ()));
        } catch (InterruptedException e) {
            break;
        }
    }
}

public void destroy () {
    super.destroy ();
    endAnimation ();
}

