public void startAnimation () {
    if (view ().drawing () instanceof Animatable && fAnimator == null) {
        fAnimator = new Animator ((Animatable) view ().drawing (), view ());
        fAnimator.start ();
    }
}

public void endAnimation () {
    if (fAnimator != null) {
        fAnimator.end ();
        fAnimator = null;
    }
}

