    fFileName = null;
    fImage = null;
    fDisplayBox = null;
}

public ImageFigure (Image image, String fileName, Point origin) {
    fFileName = fileName;
    fImage = image;
    basicDisplayBox (origin, new Point (origin.x + fImage.getWidth (this), origin.y + fImage.getHeight (this)));
}

