    for (int i = 0;
    i < lafs.length; i ++) {
        final String lnfClassName = lafs [i].getClassName ();
        Command cmd = new AbstractCommand (lafs [i].getName (), this) {
            public void execute () {
                newLookAndFeel (lnfClassName);
            }}

        ;
        menu.add (cmd);
    }
    return menu;
}

