public boolean undo () {
    if (! super.undo ()) {
        return false;
    }
    swapConnectors ();
    return true;
}

public boolean redo () {
    if (! isRedoable ()) {
        return false;
    }
    swapConnectors ();
    return true;
}

private void swapConnectors () {
    FigureEnumeration fe = getAffectedFigures ();
    if (fe.hasNextFigure ()) {
        ConnectionFigure connection = (ConnectionFigure) fe.nextFigure ();
        setOldConnector (replaceConnector (connection));
        connection.updateConnection ();
    }
}

