    List handles = CollectionsFactory.current ().createList ();
    while (hasNextHandle ()) {
        handles.add (nextHandle ());
    }
    myIterator = handles.iterator ();
    return handles;
}

public void reset () {
    myIterator = myInitialCollection.iterator ();
}

