public boolean undo () {
    if (super.undo () && getAffectedFigures ().hasNextFigure ()) {
        getDrawingView ().clearSelection ();
        setAffectedFigures (myCommand.insertFigures (getAffectedFigures (), 0, 0));
        return true;
    }
    return false;
}

