    return fBackgrounds;
}

public void removeForeground (Painter painter) {
    if (fForegrounds != null) {
        fForegrounds.remove (painter);
    }
    repaint ();
}

public void addForeground (Painter painter) {
    if (fForegrounds == null) {
        fForegrounds = CollectionsFactory.current ().createList (3);
