    if (view () == null) {
        throw new JHotDrawRuntimeException ("execute should NOT be getting called when view() == null");
    }
}

public boolean isExecutable () {
    if (isViewRequired ()) {
        if ((view () == null) || ! view ().isInteractive ()) {
            return false;
        }
    }
    return isExecutableWithView ();
}

