public void testCreateFailed () {
    try {
        exporterRegistry.createExporter ("noextension");
        fail ();
    } catch (ExporterException e) {
        assertEquals ("exporter.unknown", e.getMessage ());
    }
    try {
        exporterRegistry.createExporter ("unknown.extension");
        fail ();
    } catch (ExporterException e) {
        assertEquals ("exporter.unknown", e.getMessage ());
    }
}

