    Label label = new Label (labelsGroup, SWT.NONE);
    label.setText (Labels.getLabel ("preferences.display.labels.notAvailable"));
    notAvailableText = new Text (labelsGroup, SWT.BORDER);
    notAvailableText.setLayoutData (gridData);
