{
    toolDone ();
    String type = guessType (filename);
    if (type.equals ("storable")) {
        readFromStorableInput (filename);
    }
    else if (type.equals ("serialized")) {
        readFromObjectInput (filename);
    }
    else {
        showStatus ("Unknown file type");
    }

}

