                setEndConnector (new NullConnector (tempEndFigure));
                getConnection ().connectEnd (getEndConnector ());
                getConnection ().endPoint (ex, ey);
                getConnection ().updateConnection ();
            }
            else {
                ((PolyLineFigure) getConnection ()).addPoint (ex, ey);
