{
    int start = pos.y + 1;
    if (start >= text.length ()) {
        return null;
    }

    pos.x = start;
    int end = text.indexOf ('\n', start);
    if (end == - 1) {
        end = text.length ();
    }

    pos.y = end;
    if (text.charAt (end - 1) == '\r') {
        return text.substring (start, end - 1);
    }
    else {
        return text.substring (start, end);
    }

}

