            }
            else if (rl.equals (RelativeLocator.south ())) {
                view.setCursor (new Cursor (Cursor.S_RESIZE_CURSOR));
            }
            else if (rl.equals (RelativeLocator.southWest ())) {
                view.setCursor (new Cursor (Cursor.SW_RESIZE_CURSOR));
            }
