                view ().toggleSelection (figure);
            }
            else if (! view ().isFigureSelected (figure)) {
                view ().clearSelection ();
                view ().addToSelection (figure);
            }

        }
        else {
            if (! e.isShiftDown ()) {
                view ().clearSelection ();
            }

            fChild = createAreaTracker ();
        }

    }

    if (fChild != null) {
        fChild.mouseDown (e, x, y);
