            if (startPos != 0 && template.charAt (startPos - 1) == ESCAPE_CHAR) {
                startPos ++;
                continue;
            }

            endPos = startPos + 1;
            while ((endPos = template.indexOf (END_ENTITY_CHAR, endPos)) != - 1) {
                if (endPos == 0 || template.charAt (endPos - 1) != ESCAPE_CHAR) {
                    break;
                }

