{
    super.mouseDrag (e, x, y);
    fMoved = (Math.abs (x - getAnchorX ()) > 4) || (Math.abs (y - getAnchorY ()) > 4);
    if (fMoved) {
        FigureEnumeration figures = getUndoActivity ().getAffectedFigures ();
