        if (deleteCmd.isExecutable ()) {
            deleteCmd.execute ();
        }

    }
    else if ((code == KeyEvent.VK_DOWN) || (code == KeyEvent.VK_UP) || (code == KeyEvent.VK_RIGHT) || (code == KeyEvent.VK_LEFT)) {
        handleCursorKey (code);
