        stepX = fConstrainer.getStepX ();
        stepY = fConstrainer.getStepY ();
    }

    switch (key) {
        case KeyEvent.VK_DOWN :
            dy = stepY;
            break;
        case KeyEvent.VK_UP :
            dy = - stepY;
            break;
        case KeyEvent.VK_RIGHT :
            dx = stepX;
            break;
        case KeyEvent.VK_LEFT :
            dx = - stepX;
            break;
    }
    moveSelection (dx, dy);
