{
    Dimension d1 = edge.getStartConnector ().owner ().size ();
    Dimension d2 = edge.getEndConnector ().owner ().size ();
    int len = Math.max (d1.width, d1.height) / 2 + Math.max (d2.width, d2.height) / 2 + addlen;
    edges.put (edge, new Double (len));
