char *DeviceInfo_MountPoint_sysdep (DeviceInfo_T devinfo, char *blockdev) {
    struct mnttab mnt;
    FILE *mntfd;
    ASSERT (devinfo);
    ASSERT (blockdev);
    if ((mntfd = fopen ("/etc/mnttab", "r")) == NULL) {
        log ("%s: Cannot open /etc/mnttab file", prog);
        return NULL;
    }
    while (getmntent (mntfd, &mnt) == 0) {
        if (IS (blockdev, mnt.mnt_special)) {
            fclose (mntfd);
            return strncpy (devinfo->mntpath, mnt.mnt_mountp, sizeof (devinfo->mntpath));
        }
    }
    fclose (mntfd);
    return NULL;
}

int DeviceInfo_Usage_sysdep (DeviceInfo_T devinfo) {
    struct statvfs usage;
    ASSERT (devinfo);
    if (statvfs (devinfo->mntpath, &usage) != 0) {
        log ("%s: Error getting usage statistics for device '%s' -- %s\n", prog, devinfo -> mntpath, STRERROR);
        return FALSE;
    }
    devinfo->f_bsize = usage.f_bsize;
    devinfo->f_blocks = usage.f_blocks / (usage.f_frsize ? (usage.f_bsize / usage.f_frsize) : 1);
    devinfo->f_blocksfree = usage.f_bavail / (usage.f_frsize ? (usage.f_bsize / usage.f_frsize) : 1);
    devinfo->f_blocksfreetotal = usage.f_bfree / (usage.f_frsize ? (usage.f_bsize / usage.f_frsize) : 1);
    devinfo->f_files = usage.f_files;
    devinfo->f_filesfree = usage.f_ffree;
    return TRUE;
}

char *DeviceInfo_MountPoint_sysdep (DeviceInfo_T devinfo, char *blockdev) {
    log ("%s: Unsupported mounted filesystem information method", prog);
    return NULL;
}

int DeviceInfo_Usage_sysdep (DeviceInfo_T devinfo) {
    log ("%s: Unsupported filesystem informations gathering method\n", prog);
    return FALSE;
}

void init_env () {
    set_sandbox ();
    set_environment ();
    atexit (destroy_env);
}

void destroy_env () {
    FREE (Run.Env.cwd);
    FREE (Run.Env.home);
    FREE (Run.Env.user);
    FREE (Run.localhostname);
    FREE (Run.controlfile);
}

static void set_sandbox (void) {
    int i;
    struct stat st;
    extern char **environ;
    char *path = "PATH=/bin:/usr/bin:/sbin:/usr/sbin";
    environ[0] = 0;
    if (putenv (path)) {
        log ("%s: cannot set the PATH variable -- %s\n", prog, STRERROR);
        exit (1);
    }
    for (i = 0; i < 3; i++) {
        if (fstat (i, &st) == -1 && open ("/dev/null", O_RDWR) != i) {
            log ("Cannot open /dev/null -- %s\n", STRERROR);
            exit (1);
        }
    }
    fd_close ();
}

static void set_environment (void) {
    char cwd [STRLEN];
    struct passwd *pw;
    char localhost [STRLEN];
    if (!(pw = getpwuid (geteuid ()))) {
        log ("%s: You don't exist. Go away.\n", prog);
        exit (1);
    }
    if (!(getcwd (cwd, sizeof (cwd)))) {
        log ("%s: Cannot read current directory -- %s\n", prog, STRERROR);
        exit (1);
    }
    if (gethostname (localhost, sizeof (localhost)) < 0) {
        snprintf (localhost, STRLEN, "%s", LOCALHOST);
    }
    Run.umask = umask (0);
    Run.Env.cwd = xstrdup (cwd);
    Run.Env.home = xstrdup (pw->pw_dir);
    Run.Env.user = xstrdup (pw->pw_name);
    Run.localhostname = xstrdup (localhost);
}

void Event_post (Service_T source, long id, char *s, ... ) {
    long l;
    Event_T e;
    ASSERT (source);
    NEW (e);
    e->id = id;
    e->message = NULL;
    e->source = source;
    if (s) {
        va_list ap;
        va_start (ap, s);
        e->message = format (s, ap, &l);
        va_end (ap);
    }
    LOCK (handle_mutex) handle_event (e);
    END_LOCK;
}

void Event_free (Event_T *E) {
    ASSERT (E && * E);
    FREE ((* E) -> message);
    FREE (*E);
    (*E) = NULL;
}

Service_T Event_get_source (Event_T E) {
    ASSERT (E);
    return E->source;
}

int Event_get_id (Event_T E) {
    ASSERT (E);
    return E->id;
}

const char *Event_get_message (Event_T E) {
    ASSERT (E);
    return E->message;
}

const char *Event_get_description (Event_T E) {
    int i, event;
    int size = sizeof (Event_Table) / sizeof (Event_Table [0]);
    ASSERT (E);
    event = (E->source->mode == MODE_PASSIVE && ((E->id == EVENT_START) || (E->id == EVENT_STOP) || (E->id == EVENT_RESTART))) ? EVENT_FAILED : E->id;
    for (i = 0; i < size; i++)
        if (event == Event_Table[i].id)
            return Event_Table[i].description;
    return NULL;
}

static void handle_event (Event_T E) {
    ASSERT (E);
    if (E->message) {
        log ("%s", E -> message);
    }
    switch (E->id) {
    case EVENT_UID :
        handle_uid (E);
        break;
    case EVENT_GID :
        handle_gid (E);
        break;
    case EVENT_SIZE :
        handle_size (E);
        break;
    case EVENT_STOP :
        handle_stop (E);
        break;
    case EVENT_START :
        handle_start (E);
        break;
    case EVENT_RESTART :
        handle_restart (E);
        break;
    case EVENT_TIMEOUT :
        handle_timeout (E);
        break;
    case EVENT_RESOURCE :
        handle_resource (E);
        break;
    case EVENT_CHECKSUM :
        handle_checksum (E);
        break;
    case EVENT_TIMESTAMP :
        handle_timestamp (E);
        break;
    case EVENT_CONNECTION :
        handle_connection (E);
        break;
    case EVENT_PERMISSION :
        handle_permission (E);
        break;
    case EVENT_UNMONITOR :
        handle_unmonitor (E);
        break;
    }
    if (E->id != EVENT_NULL) {
        handle_alert (E);
    }
    Event_free (& E);
}

static void handle_stop (Event_T E) {
    if ((E->source->mode != MODE_PASSIVE)) {
        check_service (E -> source -> name, "stop");
    }
}

static void handle_start (Event_T E) {
    if (E->source->def_timeout)
        E->source->nstart++;
    if ((E->source->mode != MODE_PASSIVE)) {
        check_service (E -> source -> name, "start");
    }
}

static void handle_restart (Event_T E) {
    if (E->source->def_timeout)
        E->source->nstart++;
    if ((E->source->mode != MODE_PASSIVE)) {
        check_service (E -> source -> name, "restart");
    }
}

static void handle_timeout (Event_T E) {
    E->source->do_monitor = FALSE;
}

static void handle_resource (Event_T E) {
    Device_T d;
    Resource_T r;
    Service_T s = E->source;
    for (d = s->devicelist; d; d = d->next) {
        if (d->event_flag && d->exec != NULL) {
            d->event_flag = FALSE;
            spawn (s, d -> exec, EVENT_DESCRIPTION (E));
        }
    }
    for (r = s->resourcelist; r; r = r->next) {
        if (r->event_flag && r->exec != NULL) {
            r->event_flag = FALSE;
            spawn (s, r -> exec, EVENT_DESCRIPTION (E));
        }
    }
}

static void handle_timestamp (Event_T E) {
    Timestamp_T t;
    Service_T s = E->source;
    for (t = s->timestamplist; t; t = t->next) {
        if (t->event_flag && t->exec != NULL) {
            t->event_flag = FALSE;
            spawn (s, t -> exec, EVENT_DESCRIPTION (E));
        }
    }
}

static void handle_size (Event_T E) {
    Size_T sl;
    Service_T s = E->source;
    for (sl = s->sizelist; sl; sl = sl->next) {
        if (sl->event_flag && sl->exec != NULL) {
            sl->event_flag = FALSE;
            spawn (s, sl -> exec, EVENT_DESCRIPTION (E));
        }
    }
}

static void handle_connection (Event_T E) {
    Port_T p;
    Icmp_T i;
    Service_T s = E->source;
    for (p = s->portlist; p; p = p->next) {
        if (p->event_flag && p->exec != NULL) {
            p->event_flag = FALSE;
            spawn (s, p -> exec, EVENT_DESCRIPTION (E));
        }
    }
    for (i = s->icmplist; i; i = i->next) {
        if (i->event_flag && i->exec != NULL) {
            i->event_flag = FALSE;
            spawn (s, i -> exec, EVENT_DESCRIPTION (E));
        }
    }
}

static void handle_checksum (Event_T E) {
    Service_T s = E->source;
    if (s->checksum->event_flag && s->checksum->exec != NULL) {
        s->checksum->event_flag = FALSE;
        spawn (s, s -> checksum -> exec, EVENT_DESCRIPTION (E));
    }
}

static void handle_permission (Event_T E) {
    Service_T s = E->source;
    s->perm->has_error = TRUE;
    if (s->perm->event_flag && s->perm->exec != NULL) {
        s->perm->event_flag = FALSE;
        spawn (s, s -> perm -> exec, EVENT_DESCRIPTION (E));
    }
}

static void handle_uid (Event_T E) {
    Service_T s = E->source;
    s->uid->has_error = TRUE;
    if (s->uid->event_flag && s->uid->exec != NULL) {
        s->uid->event_flag = FALSE;
        spawn (s, s -> uid -> exec, EVENT_DESCRIPTION (E));
    }
}

static void handle_gid (Event_T E) {
    Service_T s = E->source;
    s->gid->has_error = TRUE;
    if (s->gid->event_flag && s->gid->exec != NULL) {
        s->gid->event_flag = FALSE;
        spawn (s, s -> gid -> exec, EVENT_DESCRIPTION (E));
    }
}

static void handle_unmonitor (Event_T E) {
    check_service (E -> source -> name, "unmonitor");
}

void init_files () {
    char pidfile [STRLEN];
    char statefile [STRLEN];
    if (Run.pidfile == NULL) {
        if (!getuid ()) {
            snprintf (pidfile, STRLEN, "%s/%s", MYPIDDIR, MYPIDFILE);
        }
        else {
            snprintf (pidfile, STRLEN, "%s/.%s", Run.Env.home, MYPIDFILE);
        }
        Run.pidfile = xstrdup (pidfile);
    }
    if (Run.statefile == NULL) {
        snprintf (statefile, STRLEN, "%s/.%s", Run.Env.home, MYSTATEFILE);
        Run.statefile = xstrdup (statefile);
    }
}

void finalize_files () {
    unlink (Run.pidfile);
    unlink (Run.statefile);
}

time_t get_timestamp (char *object, mode_t type) {
    struct stat buf;
    ASSERT (object);
    if (!stat (object, &buf)) {
        if (((type == S_IFREG) && S_ISREG (buf.st_mode)) || ((type == S_IFDIR) && S_ISDIR (buf.st_mode)) || ((type == (S_IFREG | S_IFDIR)) && (S_ISREG (buf.st_mode) || S_ISDIR (buf.st_mode)))) {
            return MAX (buf.st_mtime, buf.st_ctime);
        }
        else {
            log ("%s: Invalid object type - %s\n", prog, object);
        }
    }
    return FALSE;
}

char *find_rcfile () {
    char *rcfile = xmalloc (STRLEN);
    snprintf (rcfile, STRLEN, "%s/.%s", Run.Env.home, MONITRC);
    if (exist_file (rcfile)) {
        return (rcfile);
    }
    memset (rcfile, 0, STRLEN);
    snprintf (rcfile, STRLEN, "/etc/%s", MONITRC);
    if (exist_file (rcfile)) {
        return (rcfile);
    }
    if (exist_file (MONITRC)) {
        memset (rcfile, 0, STRLEN);
        snprintf (rcfile, STRLEN, "%s/%s", Run.Env.cwd, MONITRC);
        return (rcfile);
    }
    log ("%s: Cannot find the control file at ~/.%s, /etc/%s or at ./%s \n", prog, MONITRC, MONITRC, MONITRC);
    exit (1);
}

int create_pidfile (char *pidfile) {
    FILE *F = NULL;
    ASSERT (pidfile);
    umask (MYPIDMASK);
    unlink (pidfile);
    if ((F = fopen (pidfile, "w")) == (FILE *) NULL) {
        log ("%s: Error opening pidfile '%s' for writing -- %s\n", prog, pidfile, STRERROR);
        return (FALSE);
    }
    fprintf (F, "%d\n", (int) getpid ());
    fclose (F);
    return TRUE;
}

int check_rcfile (char *rcfile) {
    ASSERT (rcfile);
    return check_file_stat (rcfile, "control file", S_IRUSR | S_IWUSR | S_IXUSR);
}

int isreg_file (char *file) {
    struct stat buf;
    ASSERT (file);
    return (stat (file, &buf) == 0 && S_ISREG (buf.st_mode));
}

int exist_file (char *file) {
    struct stat buf;
    ASSERT (file);
    return (stat (file, &buf) == 0);
}

int check_file_stat (char *filename, char *description, int permmask) {
    struct stat buf;
    errno = 0;
    ASSERT (filename);
    ASSERT (description);
    if (lstat (filename, &buf) < 0) {
        log ("%s: Cannot stat the %s '%s' -- %s\n", prog, description, filename, STRERROR);
        return FALSE;
    }
    if (S_ISLNK (buf.st_mode)) {
        log ("%s: The %s '%s' must not be a symbolic link.\n", prog, description, filename);
        return (FALSE);
    }
    if (!S_ISREG (buf.st_mode)) {
        log ("%s: The %s '%s' is not a regular file.\n", prog, description, filename);
        return FALSE;
    }
    if (buf.st_uid != geteuid ()) {
        log ("%s: The %s '%s' must be owned by you.\n", prog, description, filename);
        return FALSE;
    }
    if ((buf.st_mode & 0777) & ~permmask) {
        log ("%s: The %s '%s' must have permissions no more " "than -%c%c%c%c%c%c%c%c%c (0%o); " "right now permissions are -%c%c%c%c%c%c%c%c%c (0%o).\n", prog, description, filename, permmask & S_IRUSR ? 'r' : '-', permmask & S_IWUSR ? 'w' : '-', permmask & S_IXUSR ? 'x' : '-', permmask & S_IRGRP ? 'r' : '-', permmask & S_IWGRP ? 'w' : '-', permmask & S_IXGRP ? 'x' : '-', permmask & S_IROTH ? 'r' : '-', permmask & S_IWOTH ? 'w' : '-', permmask & S_IXOTH ? 'x' : '-', permmask & 0777, buf.st_mode & S_IRUSR ? 'r' : '-', buf.st_mode & S_IWUSR ? 'w' : '-', buf.st_mode & S_IXUSR ? 'x' : '-', buf.st_mode & S_IRGRP ? 'r' : '-', buf.st_mode & S_IWGRP ? 'w' : '-', buf.st_mode & S_IXGRP ? 'x' : '-', buf.st_mode & S_IROTH ? 'r' : '-', buf.st_mode & S_IWOTH ? 'w' : '-', buf.st_mode & S_IXOTH ? 'x' : '-', buf.st_mode & 0777);
        return FALSE;
    }
    return TRUE;
}

void gc () {
    destroy_hosts_allow ();
    gc_protocols ();
    if (servicelist)
        _gc_service_list (&servicelist);
    if (Run.credentials)
        _gcath (&Run.credentials);
    if (Run.maillist)
        gc_mail_list (&Run.maillist);
    if (Run.mailservers)
        _gc_mail_server (&Run.mailservers);
    FREE (Run.mygroup);
    FREE (Run.httpsslpem);
    FREE (Run.httpsslclientpem);
    FREE (Run.bind_addr);
    FREE (Run.MailFormat.from);
    FREE (Run.MailFormat.subject);
    FREE (Run.MailFormat.message);
}

void gc_mail_list (Mail_T *m) {
    ASSERT (m);
    if ((*m)->next)
        gc_mail_list (&(*m)->next);
    FREE ((* m) -> to);
    FREE ((* m) -> from);
    FREE ((* m) -> subject);
    FREE ((* m) -> message);
    FREE ((* m) -> opt_message);
    FREE (*m);
}

static void _gc_service_list (Service_T *s) {
    ASSERT (s && * s);
    if ((*s)->next) {
        _gc_service_list (& (* s) -> next);
    }
    _gc_service (& (* s));
}

static void _gc_service (Service_T *s) {
    ASSERT (s && * s);
    if ((*s)->portlist) {
        _gcppl (& (* s) -> portlist);
    }
    if ((*s)->devicelist) {
        _gcdevice (& (* s) -> devicelist);
    }
    if ((*s)->icmplist) {
        _gcicmp (& (* s) -> icmplist);
    }
    if ((*s)->maillist) {
        gc_mail_list (& (* s) -> maillist);
    }
    if ((*s)->resourcelist) {
        _gcpql (& (* s) -> resourcelist);
    }
    if ((*s)->procinfo) {
        _gcppil (& (* s) -> procinfo);
    }
    if ((*s)->devinfo) {
        _gcpdil (& (* s) -> devinfo);
    }
    if ((*s)->timestamplist) {
        _gcptl (& (* s) -> timestamplist);
    }
    if ((*s)->sizelist) {
        _gcso (& (* s) -> sizelist);
    }
    if ((*s)->checksum) {
        _gcchecksum (& (* s) -> checksum);
    }
    if ((*s)->perm) {
        _gcperm (& (* s) -> perm);
    }
    if ((*s)->uid) {
        _gcuid (& (* s) -> uid);
    }
    if ((*s)->gid) {
        _gcgid (& (* s) -> gid);
    }
    if ((*s)->dependantlist) {
        _gcpdl (& (* s) -> dependantlist);
    }
    if ((*s)->start) {
        _gccmd (& (* s) -> start);
    }
    if ((*s)->stop) {
        _gccmd (& (* s) -> stop);
    }
    FREE ((* s) -> name);
    FREE ((* s) -> path);
    FREE ((* s) -> group);
    (*s)->next = NULL;
    pthread_mutex_destroy (& (* s) -> mutex);
    FREE (*s);
}

static void _gc_mail_server (MailServer_T *s) {
    if (!s && !*s)
        return;
    if ((*s)->next) {
        _gc_mail_server (& (* s) -> next);
    }
    FREE ((* s) -> host);
    FREE (*s);
}

static void _gccmd (Command_T *c) {
    int i;
    ASSERT (c && * c);
    for (i = 0; (*c)->arg[i]; i++)
        FREE ((*c)->arg[i]);
    FREE (*c);
}

static void _gcppl (Port_T *p) {
    ASSERT (p && * p);
    if ((*p)->next) {
        _gcppl (& (* p) -> next);
    }
    if ((*p)->exec) {
        _gccmd (& (* p) -> exec);
    }
    if ((*p)->generic) {
        _gcgrc (& (* p) -> generic);
    }
    FREE ((* p) -> address);
    FREE ((* p) -> request);
    FREE ((* p) -> hostname);
    FREE ((* p) -> pathname);
    FREE ((* p) -> SSL.certmd5);
    FREE ((* p) -> request_checksum);
    FREE (*p);
}

static void _gcdevice (Device_T *d) {
    ASSERT (d && * d);
    if ((*d)->next) {
        _gcdevice (& (* d) -> next);
    }
    if ((*d)->exec) {
        _gccmd (& (* d) -> exec);
    }
    FREE (*d);
}

static void _gcicmp (Icmp_T *i) {
    ASSERT (i && * i);
    if ((*i)->next) {
        _gcicmp (& (* i) -> next);
    }
    if ((*i)->exec) {
        _gccmd (& (* i) -> exec);
    }
    FREE (*i);
}

static void _gcpql (Resource_T *q) {
    ASSERT (q);
    if ((*q)->next) {
        _gcpql (& (* q) -> next);
    }
    if ((*q)->exec) {
        _gccmd (& (* q) -> exec);
    }
    FREE (*q);
}

static void _gcppil (ProcInfo_T *pi) {
    ASSERT (pi);
    FREE (*pi);
}

static void _gcpdil (DeviceInfo_T *di) {
    ASSERT (di);
    FREE (*di);
}

static void _gcptl (Timestamp_T *p) {
    ASSERT (p);
    if ((*p)->next) {
        _gcptl (& (* p) -> next);
    }
    if ((*p)->exec) {
        _gccmd (& (* p) -> exec);
    }
    FREE (*p);
}

static void _gcso (Size_T *s) {
    ASSERT (s);
    if ((*s)->next)
        _gcso (&(*s)->next);
    if ((*s)->exec)
        _gccmd (&(*s)->exec);
    FREE (*s);
}

static void _gcchecksum (Checksum_T *s) {
    ASSERT (s);
    if ((*s)->exec)
        _gccmd (&(*s)->exec);
    FREE ((* s) -> hash);
    FREE (*s);
}

static void _gcperm (Perm_T *s) {
    ASSERT (s);
    if ((*s)->exec)
        _gccmd (&(*s)->exec);
    FREE (*s);
}

static void _gcuid (Uid_T *s) {
    ASSERT (s);
    if ((*s)->exec)
        _gccmd (&(*s)->exec);
    FREE (*s);
}

static void _gcgid (Gid_T *s) {
    ASSERT (s);
    if ((*s)->exec)
        _gccmd (&(*s)->exec);
    FREE (*s);
}

static void _gcpdl (Dependant_T *d) {
    ASSERT (d);
    if ((*d)->next) {
        _gcpdl (& (* d) -> next);
    }
    FREE ((* d) -> dependant);
    FREE (*d);
}

static void _gcgrc (Generic_T *g) {
    ASSERT (g);
    if ((*g)->next) {
        _gcgrc (& (* g) -> next);
    }
    FREE ((* g) -> send);
    FREE ((* g) -> expect);
    FREE (*g);
}

static void _gcath (Auth_T *c) {
    ASSERT (c);
    if ((*c)->next) {
        _gcath (& (* c) -> next);
    }
    FREE ((* c) -> uname);
    FREE ((* c) -> passwd);
    FREE (*c);
}

char *encode_base64 (int size, unsigned char *src) {
    int i;
    char *out, *p;
    if (!src)
        return NULL;
    if (!size)
        size = strlen ((char *) src);
    out = xcalloc (sizeof (char), size *4 / 3 + 4);
    p = out;
    for (i = 0; i < size; i += 3) {
        unsigned char b1 = 0, b2 = 0, b3 = 0, b4 = 0, b5 = 0, b6 = 0, b7 = 0;
        b1 = src[i];
        if (i + 1 < size)
            b2 = src[i + 1];
        if (i + 2 < size)
            b3 = src[i + 2];
        b4 = b1 >> 2;
        b5 = ((b1 & 0x3) << 4) | (b2 >> 4);
        b6 = ((b2 & 0xf) << 2) | (b3 >> 6);
        b7 = b3 & 0x3f;
        *p++ = encode (b4);
        *p++ = encode (b5);
        if (i + 1 < size) {
            *p++ = encode (b6);
        }
        else {
            *p++ = '=';
        }
        if (i + 2 < size) {
            *p++ = encode (b7);
        }
        else {
            *p++ = '=';
        }
    }
    return out;
}

int decode_base64 (unsigned char *dest, const char *src) {
    if (src) {
        unsigned char *p = dest;
        int k, l = strlen (src) + 1;
        unsigned char *buf = xmalloc (l);
        for (k = 0, l = 0; src[k]; k++) {
            if (is_base64 (src[k])) {
                buf[l++] = src[k];
            }
        }
        for (k = 0; k < l; k += 4) {
            char c1 = 'A', c2 = 'A', c3 = 'A', c4 = 'A';
            unsigned char b1 = 0, b2 = 0, b3 = 0, b4 = 0;
            c1 = buf[k];
            if (k + 1 < l) {
                c2 = buf[k + 1];
            }
            if (k + 2 < l) {
                c3 = buf[k + 2];
            }
            if (k + 3 < l) {
                c4 = buf[k + 3];
            }
            b1 = decode (c1);
            b2 = decode (c2);
            b3 = decode (c3);
            b4 = decode (c4);
            *p++ = ((b1 << 2) | (b2 >> 4));
            if (c3 != '=') {
                *p++ = (((b2 & 0xf) << 4) | (b3 >> 2));
            }
            if (c4 != '=') {
                *p++ = (((b3 & 0x3) << 6) | b4);
            }
        }
        FREE (buf);
        return (p - dest);
    }
    return FALSE;
}

static char encode (unsigned char u) {
    if (u < 26)
        return 'A' + u;
    if (u < 52)
        return 'a' + (u - 26);
    if (u < 62)
        return '0' + (u - 52);
    if (u == 62)
        return '+';
    return '/';
}

static unsigned char decode (char c) {
    if (c >= 'A' && c <= 'Z')
        return (c - 'A');
    if (c >= 'a' && c <= 'z')
        return (c - 'a' + 26);
    if (c >= '0' && c <= '9')
        return (c - '0' + 52);
    if (c == '+')
        return 62;
    return 63;
}

static int is_base64 (char c) {
    if ((c >= 'A' && c <= 'Z') || (c >= 'a' && c <= 'z') || (c >= '0' && c <= '9') || (c == '+') || (c == '/') || (c == '=')) {
        return TRUE;
    }
    return FALSE;
}

void init_service () {
    add_Impl ((void *) doGet, (void *) doPost);
}

static void doPost (HttpRequest req, HttpResponse res) {
    doGet (req, res);
}

static void doGet (HttpRequest req, HttpResponse res) {
    set_content_type (res, "text/html");
    if (ACTION (HOME)) {
        LOCK (Run.mutex)
        do_home (req, res);
        END_LOCK;
    }
    else if (ACTION (RUN)) {
        LOCK (Run.mutex)
        do_runtime (req, res);
        END_LOCK;
    }
    else if (ACTION (TEST)) {
        is_monit_running (req, res);
    }
    else if (ACTION (VIEWLOG)) {
        do_viewlog (req, res);
    }
    else if (ACTION (ABOUT)) {
        do_about (req, res);
    }
    else if (ACTION (STOP)) {
        if (is_readonly (req)) {
            send_error (res, SC_FORBIDDEN, "You do not have sufficent privilegs to access this page");
        }
        else {
            send_error (res, SC_SERVICE_UNAVAILABLE, "The monit http server is stopped");
            stop_httpd ();
        }
    }
    else if (ACTION (PIXEL)) {
        printPixel (res);
    }
    else if (ACTION (STATUS)) {
        const char *format = get_parameter (req, "format");
        if (format && starts_with (format, "xml"))
            print_status (res, XML_STATUS);
        else {
            print_status (res, TEXT_STATUS);
        }
    }
    else {
        handle_action (req, res);
    }
}

static void is_monit_running (HttpRequest req, HttpResponse res) {
    int status;
    int monit = exist_daemon ();
    if (monit) {
        status = SC_OK;
    }
    else {
        status = SC_GONE;
    }
    set_status (res, status);
}

