static void do_runtime (HttpRequest req, HttpResponse res) {
    int pid = exist_daemon ();
    HEAD ("_runtime", 1000)
    out_print (res, "
monit runtime status
");
    out_print (res, "" "
Parameter" "Value");
    out_print (res, "
Host%s", Run.localhostname);
    out_print (res, "
Process id%d", pid);
    out_print (res, "
Effective user running monit" "%s", Run.Env.user);
    out_print (res, "
Controlfile%s", Run.controlfile);
    if (Run.logfile)
        out_print (res, "
Logfile%s", Run.logfile);
    out_print (res, "
Pidfile%s", Run.pidfile);
    out_print (res, "
State file%s", Run.statefile);
    out_print (res, "
Debug%s", Run.debug ? "True" : "False");
    out_print (res, "
Log%s", Run.dolog ? "True" : "False");
    out_print (res, "
Use syslog%s", Run.use_syslog ? "True" : "False");
    out_print (res, "
Mail server(s)");
    if (Run.mailservers) {
        MailServer_T mta = Run.mailservers;
        for (mta = Run.mailservers; mta; mta = mta->next)
            out_print (res, "%s ", mta->host);
        out_print (res, "");
    }
    else {
        out_print (res, "localhost");
    }
    if (Run.MailFormat.from)
        out_print (res, "
Default mail from%s", Run.MailFormat.from);
    if (Run.MailFormat.subject)
        out_print (res, "
Default mail subject%s", Run.MailFormat.subject);
    if (Run.MailFormat.message)
        out_print (res, "
Default mail message%s", Run.MailFormat.message);
    out_print (res, "
Poll time%d seconds", Run.polltime);
    out_print (res, "
httpd bind address%s", Run.bind_addr ? Run.bind_addr : "Any/All");
    out_print (res, "
httpd portnumber%d", Run.httpdport);
    out_print (res, "
httpd signature%d", Run.httpdsig ? "True" : "False");
    out_print (res, "
Use ssl encryption%s", Run.httpdssl ? "True" : "False");
    if (Run.httpdssl) {
        out_print (res, "
PEM key/certificate file%s", Run.httpsslpem);
        if (Run.httpsslclientpem != NULL) {
            out_print (res, "
Client PEM key/certification" "%s", "Enabled");
            out_print (res, "
Client PEM key/certificate file" "%s", Run.httpsslclientpem);
        }
        else {
            out_print (res, "
Client PEM key/certification" "%s", "Disabled");
        }
        out_print (res, "
Allow self certified certificates " "%s", Run.allowselfcert ? "True" : "False");
    }
    out_print (res, "
httpd auth. style%s", (Run.credentials != NULL) && has_hosts_allow () ? "Basic Authentication and Host allow list" : (Run.credentials != NULL) ? "Basic Authentication" : has_hosts_allow () ? "Host allow list" : "No authentication");
    print_alerts (res, Run.maillist);
    out_print (res, "");
    if (!is_readonly (req)) {
        out_print (res, "
" "Stop monit http server?" "" "" "", STOP);
        if (Run.dolog && !Run.use_syslog) {
            out_print (res, "" "View monit logfile?" "" "" "", VIEWLOG);
        }
    }
    out_print (res, "");
    FOOT
}

static void do_viewlog (HttpRequest req, HttpResponse res) {
    if (is_readonly (req)) {
        send_error (res, SC_FORBIDDEN, "You do not have sufficent privilegs to access this page");
        return;
    }
    HEAD ("_viewlog", 100)
    if (Run.dolog && !Run.use_syslog) {
        struct stat sb;
        if (!stat (Run.logfile, &sb)) {
            FILE *f = fopen (Run.logfile, "r");
            if (f) {
                int n;
                char buf [8192];
                out_print (res, "

");
                while ((n = fread (buf, sizeof (char), 8192, f)) > 0) {
                    buf[n] = 0;
                    out_print (res, "%s", buf);
                }
                fclose (f);
                out_print (res, "");
            }
            else {
                out_print (res, "Error opening logfile: %s", STRERROR);
            }
        }
        else {
            out_print (res, "Error stating logfile: %s", STRERROR);
        }
    }
    else {
        out_print (res, "Cannot view logfile:
");
        if (!Run.dolog) {
            out_print (res, "monit was started without logging");
        }
        else {
            out_print (res, "monit uses syslog");
        }
    }
    FOOT
}

static void handle_action (HttpRequest req, HttpResponse res) {
    char *name = req->url;
    const char *action = get_parameter (req, "action");
    if (exist_service (++name)) {
        if (action) {
            Service_T s = get_service (name);
            if (is_readonly (req)) {
                send_error (res, SC_FORBIDDEN, "You do not have sufficent privilegs to access this page");
                return;
            }
            if (IS (action, "start")) {
                if (s->start) {
                    LOCK (s -> mutex)
                    check_service (name, action);
                    if (s->type == TYPE_PROCESS) {
                        int max_tries = Run.polltime;
                        while (max_tries-- && !Run.stopped) {
                            if (is_process_running (s))
                                break;
                            sleep (1);
                        }
                    }
                    END_LOCK;
                    if (s->type == TYPE_PROCESS && !is_process_running (s)) {
                        send_error (res, SC_INTERNAL_SERVER_ERROR, "Could not start the service");
                        goto quit;
                    }
                }
                else {
                    send_error (res, SC_BAD_REQUEST, "Start method not defined for the service");
                    goto quit;
                }
            }
            else if (IS (action, "stop")) {
                if (s->stop) {
                    LOCK (s -> mutex)
                    check_service (name, action);
                    END_LOCK;
                }
                else {
                    send_error (res, SC_BAD_REQUEST, "Stop method not defined for the service");
                    goto quit;
                }
            }
            else if (IS (action, "restart")) {
                if (s->start && s->stop) {
                    LOCK (s -> mutex)
                    check_service (name, action);
                    if (s->type == TYPE_PROCESS) {
                        int max_tries = Run.polltime;
                        while (max_tries-- && !Run.stopped) {
                            if (is_process_running (s))
                                break;
                            sleep (1);
                        }
                    }
                    END_LOCK;
                    if (s->type == TYPE_PROCESS && !is_process_running (s)) {
                        send_error (res, SC_INTERNAL_SERVER_ERROR, "Could not restart the service");
                        goto quit;
                    }
                }
                else {
                    send_error (res, SC_BAD_REQUEST, "Start or stop method not defined for the service");
                    goto quit;
                }
            }
            else if (IS (action, "monitor")) {
                LOCK (s -> mutex)
                check_service (name, action);
                END_LOCK;
            }
            else if (IS (action, "unmonitor")) {
                LOCK (s -> mutex)
                check_service (name, action);
                END_LOCK;
            }
        }
        LOCK (Run.mutex)
        do_service (req, res, name);
        END_LOCK;
    }
    else {
        not_found (req, res);
    }
quit :
    reset_depend ();
}

static void do_service (HttpRequest req, HttpResponse res, char *name) {
    Service_T s = get_service (name);
    HEAD (name, 1000)
    switch (s->type) {
    case TYPE_DEVICE :
        do_service_device (req, res, s);
        break;
    case TYPE_DIRECTORY :
        do_service_directory (req, res, s);
        break;
    case TYPE_FILE :
        do_service_file (req, res, s);
        break;
    case TYPE_PROCESS :
        do_service_process (req, res, s);
        break;
    case TYPE_REMOTE :
        do_service_host (req, res, s);
        break;
    default :
        break;
    }
    FOOT
}

static void printPixel (HttpResponse res) {
    static int l;
    Socket_T S = res->S;
    static unsigned char *pixel = NULL;
    if (!pixel) {
        pixel = xcalloc (sizeof (unsigned char), strlen (PIXEL_GIF));
        l = decode_base64 (pixel, PIXEL_GIF);
    }
    if (l) {
        res->is_committed = TRUE;
        socket_print (S, "HTTP/1.0 200 OK\r\n");
        socket_print (S, "Content-length: %d\r\n", l);
        socket_print (S, "Content-Type: image/gif\r\n");
        socket_print (S, "Connection: close\r\n\r\n");
        socket_write (S, pixel, l);
    }
}

static void not_found (HttpRequest req, HttpResponse res) {
    send_error (res, SC_NOT_FOUND, "The requested URL was not found on this server");
}

static void do_home_process (HttpRequest req, HttpResponse res) {
    int on = TRUE;
    int header = TRUE;
    Service_T s;
    for (s = servicelist_conf; s; s = s->next_conf) {
        int isrunning;
        char *uptime;
        if (s->type != TYPE_PROCESS)
            continue;
        if (header) {
            if (Run.doprocess) {
                out_print (res, "

 " "" "
" "
Process" "
Status" "
Uptime" "
CPU" "
Memory" "");
            }
            else {
                out_print (res, "

 " "" "
" "
Service" "
Status" "");
            }
            header = FALSE;
        }
        isrunning = is_process_running (s);
        uptime = get_process_uptime (s->path, " ");
        if (Run.doprocess) {
            ProcInfo_T pi = s->procinfo;
            if (isrunning) {
                out_print (res, "
" "%s" "%s%s" "%.1f%%" "%.1f%% [%ld kB]", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, ! s -> do_monitor ? "not monitored" : isrunning ? "running" : "not running", uptime, pi -> cpu_percent / 10.0, pi -> mem_percent / 10.0, pi -> mem_kbyte);
            }
            else {
                out_print (res, "
" "%s" "%s" "-" "- " "- [-]", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, ! s -> do_monitor ? "not monitored" : "not running");
            }
        }
        else {
            out_print (res, "
" "%s" "%s %s" "", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, ! s -> do_monitor ? "not monitored" : isrunning ? "running" : "not running", isrunning ? uptime : "");
        }
        on = on ? FALSE : TRUE;
        FREE (uptime);
    }
    if (!header)
        out_print (res, "");
}

static void do_home_device (HttpRequest req, HttpResponse res) {
    Service_T s;
    int on = TRUE;
    int header = TRUE;
    for (s = servicelist_conf; s; s = s->next_conf) {
        if (s->type != TYPE_DEVICE)
            continue;
        if (header) {
            out_print (res, "

 " "" "
" "
Device" "
Status" "
Space usage" "
Inodes usage" "");
            header = FALSE;
        }
        if (!DeviceInfo_Usage (s->devinfo, s->path)) {
            out_print (res, "
" "%s" "%s" "- [-]" "- [-]" "", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, ! s -> do_monitor ? "not monitored" : "not accessible");
        }
        else {
            if (s->devinfo->f_files > 0) {
                out_print (res, "
" "%s" "%s" "%.1f%% [%.1f MB]" "%.1f%% [%ld objects]" "", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, (s -> perm && s -> perm -> has_error) ? "Permission Error" : (s -> uid && s -> uid -> has_error) ? "UID Error" : ! s -> do_monitor ? "not monitored" : (s -> gid && s -> gid -> has_error) ? "GID Error" : "accessible", (float) 100 * (s -> devinfo -> f_blocks - s -> devinfo -> f_blocksfreetotal) / s -> devinfo -> f_blocks, (float) (s -> devinfo -> f_blocks - s -> devinfo -> f_blocksfreetotal) / 1048576 * s -> devinfo -> f_bsize, (float) 100 * (s -> devinfo -> f_files - s -> devinfo -> f_filesfree) / s -> devinfo -> f_files, s -> devinfo -> f_files - s -> devinfo -> f_filesfree);
            }
            else {
                out_print (res, "
" "%s" "%s" "%.1f%% [%.1f MB]" "not supported by " "filesystem", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, (s -> perm && s -> perm -> has_error) ? "Permission Error" : (s -> uid && s -> uid -> has_error) ? "UID Error" : ! s -> do_monitor ? "not monitored" : (s -> gid && s -> gid -> has_error) ? "GID Error" : "accessible", (float) 100 * (s -> devinfo -> f_blocks - s -> devinfo -> f_blocksfreetotal) / s -> devinfo -> f_blocks, (float) (s -> devinfo -> f_blocks - s -> devinfo -> f_blocksfreetotal) / 1048576 * s -> devinfo -> f_bsize);
            }
        }
        on = on ? FALSE : TRUE;
    }
    if (!header)
        out_print (res, "");
}

static void do_home_file (HttpRequest req, HttpResponse res) {
    Service_T s;
    int on = TRUE;
    int header = TRUE;
    struct stat stat_buf;
    for (s = servicelist_conf; s; s = s->next_conf) {
        if (s->type != TYPE_FILE)
            continue;
        if (header) {
            out_print (res, "

 " "" "
" "
File" "
Status" "
Size" "
Permission" "
UID" "
GID" "");
            header = FALSE;
        }
        if ((stat (s->path, &stat_buf) != 0) || !S_ISREG (stat_buf.st_mode)) {
            out_print (res, "
" "%s" "%s" "-" "-" "-" "-" "", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, ! s -> do_monitor ? "not monitored" : "doesn't exist");
        }
        else {
            out_print (res, "
" "%s" "%s" "%lu B" "%o" "%d" "%d" "", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, (s -> perm && s -> perm -> has_error) ? "Permission Error" : (s -> uid && s -> uid -> has_error) ? "UID Error" : ! s -> do_monitor ? "not monitored" : (s -> gid && s -> gid -> has_error) ? "GID Error" : "accessible", (unsigned long) stat_buf.st_size, stat_buf.st_mode & 07777, stat_buf.st_uid, stat_buf.st_gid);
        }
        on = on ? FALSE : TRUE;
    }
    if (!header)
        out_print (res, "");
}

static void do_home_directory (HttpRequest req, HttpResponse res) {
    Service_T s;
    int on = TRUE;
    int header = TRUE;
    struct stat stat_buf;
    for (s = servicelist_conf; s; s = s->next_conf) {
        if (s->type != TYPE_DIRECTORY)
            continue;
        if (header) {
            out_print (res, "

 " "" "
" "
Directory" "
Status" "
Permission" "
UID" "
GID" "");
            header = FALSE;
        }
        if ((stat (s->path, &stat_buf) != 0) || !S_ISDIR (stat_buf.st_mode)) {
            out_print (res, "
" "%s" "%s" "-" "-" "-" "", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, ! s -> do_monitor ? "not monitored" : "doesn't exist");
        }
        else {
            out_print (res, "
" "%s" "%s" "%o" "%d" "%d" "", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name, (s -> perm && s -> perm -> has_error) ? "Permission Error" : (s -> uid && s -> uid -> has_error) ? "UID Error" : ! s -> do_monitor ? "not monitored" : (s -> gid && s -> gid -> has_error) ? "GID Error" : "accessible", stat_buf.st_mode & 07777, stat_buf.st_uid, stat_buf.st_gid);
        }
