static void do_home_remote (HttpRequest req, HttpResponse res) {
    Port_T port;
    Service_T s;
    Icmp_T icmp;
    int on = TRUE;
    int header = TRUE;
    for (s = servicelist_conf; s; s = s->next_conf) {
        if (s->type != TYPE_REMOTE)
            continue;
        if (header) {
            out_print (res, "

 " "" "
" "
Host" "
Status" "
Protocol(s)" "");
            header = FALSE;
        }
        out_print (res, "
%s", on ? "bgcolor=\"#EFEFEF\"" : "", s -> name, s -> name);
        if (s->portlist) {
            int available = TRUE;
            for (port = s->portlist; port; port = port->next) {
                if (!port->is_available) {
                    available = FALSE;
                    break;
                }
            }
            if (available) {
                out_print (res, "%s" "", ! s -> do_monitor ? "not monitored" : "available");
                for (port = s->portlist; port; port = port->next) {
                    if (port != s->portlist)
                        out_print (res, "  |  ");
                    out_print (res, "[%s] at port %d", port -> protocol -> name, port -> port);
                }
                out_print (res, "");
            }
            else {
                out_print (res, "%s" "[%s] at port %d", ! s -> do_monitor ? "not monitored" : "not available", port -> protocol -> name, port -> port);
            }
        }
        else if (s->icmplist) {
            int available = TRUE;
            for (icmp = s->icmplist; icmp; icmp = icmp->next) {
                if (!icmp->is_available) {
                    available = FALSE;
                    break;
                }
            }
            if (available) {
                out_print (res, "" "available" "[ICMP PING]");
            }
            else {
                out_print (res, "" "not available" "[ICMP PING]");
            }
        }
        out_print (res, "");
        on = on ? FALSE : TRUE;
    }
    if (!header)
        out_print (res, "");
}

static void do_service_device (HttpRequest req, HttpResponse res, Service_T s) {
    struct stat stat_buf;
    int state = (DeviceInfo_Usage (s->devinfo, s->path) && stat (s->path, &stat_buf) == 0);
    out_print (res, "


Device status
");
    out_print (res, "" "
Parameter" "Value");
    out_print (res, "
Name%s", s -> name);
    out_print (res, "
Device status%s", (s -> perm && s -> perm -> has_error) ? "Permission error" : (s -> uid && s -> uid -> has_error) ? "UID Error" : (s -> gid && s -> gid -> has_error) ? "GID Error" : state ? "Accessible" : "Not Accessible");
    out_print (res, "
Path%s", s -> path);
    print_service_common_params (res, s);
    if (s->perm)
        out_print (res, "
Associated permission" "if failed %o then %s", s->perm->perm, actionnames[s->perm->action]);
    if (s->uid)
        out_print (res, "
Associated UIDif failed %d then %s" "", (int) s->uid->uid, actionnames[s->uid->action]);
    if (s->gid)
        out_print (res, "
Associated GIDif failed %d then %s" "", (int) s->gid->gid, actionnames[s->gid->action]);
    out_print (res, "
Permission%s%o", (s -> perm && s -> perm -> has_error) ? "" : "", stat_buf.st_mode & 07777);
    out_print (res, "
UID%s%d", (s -> uid && s -> uid -> has_error) ? "" : "", (int) stat_buf.st_uid);
    out_print (res, "
GID%s%d", (s -> gid && s -> gid -> has_error) ? "" : "", (int) stat_buf.st_gid);
    {
        Device_T dl;
        if (s->devicelist) {
            for (dl = s->devicelist; dl; dl = dl->next) {
                if (dl->resource == RESOURCE_ID_INODE) {
                    out_print (res, "
Inodes usage limitif %s %ld %s " "then %s", operatornames [dl -> operator], (dl -> limit_absolute > - 1) ? dl -> limit_absolute : dl -> limit_percent, (dl -> limit_absolute > - 1) ? "" : "%", actionnames [dl -> action]);
                }
                else if (dl->resource == RESOURCE_ID_SPACE) {
                    out_print (res, "
Space usage limitif %s %ld %s " "then %s", operatornames [dl -> operator], (dl -> limit_absolute > - 1) ? dl -> limit_absolute : dl -> limit_percent, (dl -> limit_absolute > - 1) ? "blocks" : "%", actionnames [dl -> action]);
                }
            }
        }
        if (state) {
            out_print (res, "
Blocks total%ld [%.1f MB]", s -> devinfo -> f_blocks, (float) s -> devinfo -> f_blocks / 1048576 * s -> devinfo -> f_bsize);
            out_print (res, "
Blocks free for non superuser" "%ld [%.1f MB] [%.1f%%]", s -> devinfo -> f_blocksfree, (float) s -> devinfo -> f_blocksfree / 1048576 * s -> devinfo -> f_bsize, (float) 100 * s -> devinfo -> f_blocksfree / s -> devinfo -> f_blocks);
            out_print (res, "
Blocks free total%ld [%.1f MB] [%.1f%%]" "", s -> devinfo -> f_blocksfreetotal, (float) s -> devinfo -> f_blocksfreetotal / 1048576 * s -> devinfo -> f_bsize, (float) 100 * s -> devinfo -> f_blocksfreetotal / s -> devinfo -> f_blocks);
            out_print (res, "
Block size%ld B", s -> devinfo -> f_bsize);
            if (s->devinfo->f_files > 0) {
                out_print (res, "
Inodes total%ld", s -> devinfo -> f_files);
                out_print (res, "
Inodes free%ld [%.1f%%]" "", s -> devinfo -> f_filesfree, (float) 100 * s -> devinfo -> f_filesfree / s -> devinfo -> f_files);
            }
        }
        else {
            out_print (res, "
Blocks total-");
            out_print (res, "
Blocks free for non superuser" "-");
            out_print (res, "
Blocks free total-");
            out_print (res, "
Block size-");
            out_print (res, "
Inodes total-");
            out_print (res, "
Inodes free-");
        }
    }
    print_alerts (res, s -> maillist);
    out_print (res, "");
    print_buttons (req, res, s);
}

static void do_service_directory (HttpRequest req, HttpResponse res, Service_T s) {
    struct stat stat_buf;
    int state = ((stat (s->path, &stat_buf) == 0) && S_ISDIR (stat_buf.st_mode));
    out_print (res, "


Directory status
");
    out_print (res, "" "
Parameter" "Value");
    out_print (res, "
Name%s", s -> name);
    out_print (res, "
Directory status%s", (s -> perm && s -> perm -> has_error) ? "Permission Error" : (s -> uid && s -> uid -> has_error) ? "UID Error" : (s -> gid && s -> gid -> has_error) ? "GID Error" : state ? "Exist" : "Doesn't Exist");
    out_print (res, "
Path%s", s -> path);
    print_service_common_params (res, s);
    {
        struct mytimestamp *t;
        for (t = s->timestamplist; t; t = t->next) {
            if (t->test_changes) {
                out_print (res, "
Associated timestamp" "If changed then %s", actionnames [t -> action]);
            }
            else {
                out_print (res, "
Associated timestamp" "If %s %d second(s) then %s", operatornames [t -> operator], t -> time, actionnames [t -> action]);
            }
        }
    }
    if (s->perm)
        out_print (res, "
Associated permission" "if failed %o then %s", s->perm->perm, actionnames[s->perm->action]);
    if (s->uid)
        out_print (res, "
Associated UID" "if failed %d then %s", (int) s->uid->uid, actionnames[s->uid->action]);
    if (s->gid)
        out_print (res, "
Associated GID" "if failed %d then %s", (int) s->gid->gid, actionnames[s->gid->action]);
    out_print (res, "
Permission%s%o", (s -> perm && s -> perm -> has_error) ? "" : "", stat_buf.st_mode & 07777);
    out_print (res, "
UID%s%d", (s -> uid && s -> uid -> has_error) ? "" : "", (int) stat_buf.st_uid);
    out_print (res, "
GID%s%d", (s -> gid && s -> gid -> has_error) ? "" : "", (int) stat_buf.st_gid);
    print_alerts (res, s -> maillist);
    out_print (res, "");
    print_buttons (req, res, s);
}

static void do_service_file (HttpRequest req, HttpResponse res, Service_T s) {
    struct stat stat_buf;
    int state = ((stat (s->path, &stat_buf) == 0) && S_ISREG (stat_buf.st_mode));
    out_print (res, "


File status
");
    out_print (res, "" "
Parameter" "Value");
    out_print (res, "
Name%s", s -> name);
    out_print (res, "
File status%s", (s -> perm && s -> perm -> has_error) ? "Permission Error" : (s -> uid && s -> uid -> has_error) ? "UID Error" : (s -> gid && s -> gid -> has_error) ? "GID Error" : state ? "Exist" : "Doesn't Exist");
    out_print (res, "
Path%s", s -> path);
    print_service_common_params (res, s);
    {
        struct mytimestamp *t;
        for (t = s->timestamplist; t; t = t->next) {
            if (t->test_changes) {
                out_print (res, "
Associated timestamp" "If changed then %s", actionnames [t -> action]);
            }
            else {
                out_print (res, "
Associated timestamp" "If %s %d second(s) then %s", operatornames [t -> operator], t -> time, actionnames [t -> action]);
            }
        }
    }
    {
        Size_T sl;
        for (sl = s->sizelist; sl; sl = sl->next) {
            if (sl->test_changes) {
                out_print (res, "
Associated size" "If changed then %s", actionnames [sl -> action]);
            }
            else {
                out_print (res, "
Associated size" "If %s %lu byte(s) then %s", operatornames [sl -> operator], sl -> size, actionnames [sl -> action]);
            }
        }
    }
    if (s->checksum) {
        switch (s->checksum->type) {
        case HASH_MD5 :
            out_print (res, "
Associated checksum" "if failed MD5(%s) then %s", s->checksum->hash, actionnames[s->checksum->action]);
            break;
        case HASH_SHA1 :
            out_print (res, "
Associated checksum" "if failed SHA1(%s) then %s", s->checksum->hash, actionnames[s->checksum->action]);
            break;
        default :
            out_print (res, "
Associated checksum" "if failed UNKNOWN(%s) then %s", s->checksum->hash, actionnames[s->checksum->action]);
            break;
        }
    }
    if (s->perm)
        out_print (res, "
Associated permission" "if failed %o then %s", s->perm->perm, actionnames[s->perm->action]);
    if (s->uid)
        out_print (res, "
Associated UID" "if failed %d then %s", (int) s->uid->uid, actionnames[s->uid->action]);
    if (s->gid)
        out_print (res, "
Associated GID" "if failed %d then %s", (int) s->gid->gid, actionnames[s->gid->action]);
    out_print (res, "
Size%lu B", (unsigned long) stat_buf.st_size);
    out_print (res, "
Permission%s%o", (s -> perm && s -> perm -> has_error) ? "" : "", stat_buf.st_mode & 07777);
    out_print (res, "
UID%s%d", (s -> uid && s -> uid -> has_error) ? "" : "", (int) stat_buf.st_uid);
    out_print (res, "
GID%s%d", (s -> gid && s -> gid -> has_error) ? "" : "", (int) stat_buf.st_gid);
    print_alerts (res, s -> maillist);
    out_print (res, "");
    print_buttons (req, res, s);
}

static void do_service_process (HttpRequest req, HttpResponse res, Service_T s) {
    int run = is_process_running (s);
    out_print (res, "


Process status
");
    out_print (res, "" "
Parameter" "Value");
    out_print (res, "
Name%s", s -> name);
    out_print (res, "
Process id %d", run);
    out_print (res, "
Process status%s", run ? "Running" : "Not Running");
    out_print (res, "
Pid file%s", s -> path);
    print_service_common_params (res, s);
    if (Run.doprocess) {
        ProcInfo_T pi = s->procinfo;
        out_print (res, "
CPU usage%.1f%%", pi -> cpu_percent / 10.0);
        out_print (res, "
Memory usage%.1f%% [%ldkB]", pi -> mem_percent / 10.0, pi -> mem_kbyte);
        out_print (res, "
Children%d", pi -> children);
        out_print (res, "
Total memory usage (incl. children) " "%.1f%% [%ldkB]", pi -> total_mem_percent / 10.0, pi -> total_mem_kbyte);
    }
    {
        Port_T n;
        for (n = s->portlist; n; n = n->next) {
            if (n->family == AF_INET) {
                if (n->SSL.use_ssl) {
                    out_print (res, "
Host:Port" "if failed %s:%d%s [%s via SSL] then %s" "", n -> hostname, n -> port, n -> request ? n -> request : "", n -> protocol -> name, actionnames [n -> action]);
                    if (n->SSL.certmd5 != NULL) {
                        out_print (res, "
Server certificate md5 sum%s", n -> SSL.certmd5);
                    }
                }
                else {
                    out_print (res, "
Host:Port" "if failed %s:%d%s [%s] then %s", n -> hostname, n -> port, n -> request ? n -> request : "", n -> protocol -> name, actionnames [n -> action]);
                }
            }
            else if (n->family == AF_UNIX) {
                out_print (res, "
Unix Socket" "if failed %s [%s] then %s", n -> pathname, n -> protocol -> name, actionnames [n -> action]);
            }
        }
    }
    {
        Resource_T q;
        for (q = s->resourcelist; q; q = q->next) {
            switch (q->resource_id) {
            case RESOURCE_ID_CPU_PERCENT :
                out_print (res, "
CPU usage limit" "If %s %.1f%% for %d cycle(s) then %s", operatornames[q->operator], q->limit / 10.0, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_MEM_PERCENT :
                out_print (res, "
Memory usage limit" "If %s %.1f%% for %d cycle(s) then %s", operatornames[q->operator], q->limit / 10.0, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_MEM_KBYTE :
                out_print (res, "
Memory amount limit" "If %s %ld for %d cycle(s) then %s", operatornames[q->operator], q->limit, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_LOAD1 :
                out_print (res, "
Load average (1min)" "If %s %.1f for %d cycle(s) then %s", operatornames[q->operator], q->limit / 10.0, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_LOAD5 :
                out_print (res, "
Load average (5min)" "If %s %.1f for %d cycle(s) then %s", operatornames[q->operator], q->limit / 10.0, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_LOAD15 :
                out_print (res, "
Load average (15min)" "If %s %.1f for %d cycle(s) then %s", operatornames[q->operator], q->limit / 10.0, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_CHILDREN :
                out_print (res, "
Children" "If %s %d for %d cycle(s) then %s", operatornames[q->operator], q->limit, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_TOTAL_MEM_KBYTE :
                out_print (res, "
Memory amount limit (incl. children)" "If %s %d for %d cycle(s) then %s", operatornames[q->operator], q->limit, q->max_cycle, actionnames[q->action]);
                break;
            case RESOURCE_ID_TOTAL_MEM_PERCENT :
                out_print (res, "
Memory usage limit (incl. children)" "If %s %.1f%% for %d cycle(s) then %s", operatornames[q->operator], q->limit / 10.0, q->max_cycle, actionnames[q->action]);
                break;
            }
        }
    }
    print_alerts (res, s -> maillist);
    out_print (res, "");
    print_buttons (req, res, s);
}

static void do_service_host (HttpRequest req, HttpResponse res, Service_T s) {
    Icmp_T icmp;
    Port_T port;
    int available = TRUE;
    for (icmp = s->icmplist; icmp; icmp = icmp->next) {
        if (!icmp->is_available) {
            available = FALSE;
            break;
        }
    }
    for (port = s->portlist; port; port = port->next) {
        if (!port->is_available) {
            available = FALSE;
            break;
        }
    }
    out_print (res, "


Host status
");
    out_print (res, "" "
Parameter" "Value");
    out_print (res, "
Name%s", s -> name);
    out_print (res, "
Remote status%s", available ? "Available" : "Not available");
    print_service_common_params (res, s);
    {
        Port_T n;
        for (n = s->portlist; n; n = n->next) {
            if (n->family == AF_INET) {
                if (n->SSL.use_ssl) {
                    out_print (res, "
Host:Port" "if failed %s:%d%s [%s via SSL] then %s", n -> hostname, n -> port, n -> request ? n -> request : "", n -> protocol -> name, actionnames [n -> action]);
                    if (n->SSL.certmd5 != NULL) {
                        out_print (res, "
Server certificate md5 sum%s", n -> SSL.certmd5);
                    }
                }
                else {
                    out_print (res, "
Host:Port" "if failed %s:%d%s [%s] then %s", n -> hostname, n -> port, n -> request ? n -> request : "", n -> protocol -> name, actionnames [n -> action]);
                }
            }
            else if (n->family == AF_UNIX) {
                out_print (res, "
Unix Socket" "if failed %s [%s] then %s", n -> pathname, n -> protocol -> name, actionnames [n -> action]);
            }
        }
    }
    {
        Icmp_T n;
        for (n = s->icmplist; n; n = n->next)
            out_print (res, "
ICMP" "if failed %s with timeout %d seconds then %s", icmpnames[n->type], n->timeout, actionnames[n->action]);
    }
    print_alerts (res, s -> maillist);
    out_print (res, "");
    print_buttons (req, res, s);
}

static void print_alerts (HttpResponse res, Mail_T s) {
    Mail_T r;
    for (r = s; r; r = r->next) {
        out_print (res, "
Alert mail to" "%s", r -> to ? r -> to : "");
        out_print (res, "
Alert on");
        if (r->events == (~((unsigned int) 0))) {
            out_print (res, "All events");
        }
        else {
            if (IS_EVENT_SET (r->events, EVENT_START))
                out_print (res, "Start ");
            if (IS_EVENT_SET (r->events, EVENT_STOP))
                out_print (res, "Stop ");
            if (IS_EVENT_SET (r->events, EVENT_RESTART))
                out_print (res, "Restart ");
            if (IS_EVENT_SET (r->events, EVENT_CHECKSUM))
                out_print (res, "Checksum ");
            if (IS_EVENT_SET (r->events, EVENT_RESOURCE))
                out_print (res, "Resource ");
            if (IS_EVENT_SET (r->events, EVENT_TIMEOUT))
                out_print (res, "Timeout ");
            if (IS_EVENT_SET (r->events, EVENT_TIMESTAMP))
                out_print (res, "Timestamp ");
            if (IS_EVENT_SET (r->events, EVENT_SIZE))
                out_print (res, "Size ");
            if (IS_EVENT_SET (r->events, EVENT_CONNECTION))
                out_print (res, "Connection ");
            if (IS_EVENT_SET (r->events, EVENT_PERMISSION))
                out_print (res, "Permission ");
            if (IS_EVENT_SET (r->events, EVENT_UID))
                out_print (res, "Uid ");
            if (IS_EVENT_SET (r->events, EVENT_GID))
                out_print (res, "Gid ");
            if (IS_EVENT_SET (r->events, EVENT_UNMONITOR))
                out_print (res, "Unmonitor ");
        }
        out_print (res, "");
    }
}

static void print_buttons (HttpRequest req, HttpResponse res, Service_T s) {
    char *action [] = {"monitor", "unmonitor"};
    char *label [] = {"Enable monitoring", "Disable monitoring"};
    if (is_readonly (req)) {
        return;
    }
    out_print (res, "
");
    if (s->start)
        out_print (res, "" "" "", s->name);
    if (s->stop)
        out_print (res, "" "" "", s->name);
    if (s->start && s->stop)
        out_print (res, "" "" "", s->name);
    out_print (res, "" "" "" "", s -> name, action [s -> do_monitor], label [s -> do_monitor]);
}

static void print_service_common_params (HttpResponse res, Service_T s) {
    Dependant_T d;
    if (s->group)
        out_print (res, "
Group%s" "", s->group);
    out_print (res, "
Monitoring mode%s", modenames [s -> mode]);
    out_print (res, "
Monitoring status%s" "", statusnames [s -> do_monitor]);
    for (d = s->dependantlist; d; d = d->next) {
        if (d->dependant != NULL) {
            out_print (res, "
Depends on service " "  %s ", d -> dependant, d -> dependant);
        }
    }
    if (s->start) {
        int i = 0;
        out_print (res, "
Start program");
        while (s->start->arg[i])
            out_print (res, "%s ", s->start->arg[i++]);
        out_print (res, "");
    }
    if (s->stop) {
        int i = 0;
        out_print (res, "
Stop program");
        while (s->stop->arg[i])
            out_print (res, "%s ", s->stop->arg[i++]);
        out_print (res, "");
    }
    out_print (res, "
Check serviceEvery %d cycle", s -> every ? s -> every : 1);
    if (s->def_timeout) {
        out_print (res, "
Timeout" "Timeout if %d restart within %d cycles", s -> to_start, s -> to_cycle);
    }
}

static int is_readonly (HttpRequest req) {
    if (req->remote_user) {
        return ((Auth_T) get_user_credentials (req->remote_user))->is_readonly;
    }
    return FALSE;
}

static void print_status (HttpResponse res, int format) {
    Service_T s;
    set_content_type (res, "text/plain");
    status_start (res, format);
    for (s = servicelist_conf; s; s = s->next_conf) {
        if (format == XML_STATUS) {
            status_service_xml (s, res);
        }
        else {
            status_service_text (s, res);
        }
    }
    status_stop (res, format);
}

static void status_start (HttpResponse res, int format) {
    char *uptime = get_process_uptime (Run.pidfile, " ");
    if (format == XML_STATUS) {
        out_print (res, "\r\n");
        out_print (res, "\r\n\t\r\n\t\t%s" "\r\n\t\t%s\r\n\t\r\n", VERSION, trim (uptime));
    }
    else {
        out_print (res, "The monit daemon uptime: %s\n\n", uptime);
    }
    FREE (uptime);
}

static void status_stop (HttpResponse res, int format) {
    if (format == XML_STATUS)
        out_print (res, "\r\n");
}

static void status_service_xml (Service_T s, HttpResponse res) {
    out_print (res, "	
		%s
		%s" "
		%d
		%d" "
	
", status_text [s -> type].type, s -> name, get_service_status (s), s -> do_monitor);
}

static void status_service_text (Service_T s, HttpResponse res) {
    char prefix [STRLEN];
    snprintf (prefix, STRLEN, "%s '%s'", status_text [s -> type].type, s -> name);
    out_print (res, "%-35s %s\n", prefix, ! s -> do_monitor ? "not monitored" : get_service_status (s) ? status_text [s -> type].up : status_text [s -> type].down);
}

static int get_service_status (Service_T s) {
    struct stat stat_buf;
    switch (s->type) {
    case TYPE_PROCESS :
        return (is_process_running (s) > 0);
        break;
    case TYPE_DEVICE :
        if (DeviceInfo_Usage (s->devinfo, s->path) && stat (s->path, &stat_buf) == 0) {
            if (s->uid && s->uid->has_error)
                return FALSE;
            else if (s->gid && s->gid->has_error)
                return FALSE;
            else
                return TRUE;
        }
        else {
            return FALSE;
        }
        break;
    case TYPE_FILE :
        if ((stat (s->path, &stat_buf) == 0) && S_ISREG (stat_buf.st_mode)) {
            if (s->perm && s->perm->has_error)
                return FALSE;
            else if (s->uid && s->uid->has_error)
                return FALSE;
            else if (s->gid && s->gid->has_error)
                return FALSE;
            else
                return TRUE;
        }
        else {
            return FALSE;
        }
        break;
    case TYPE_DIRECTORY :
        if ((stat (s->path, &stat_buf) == 0) && S_ISDIR (stat_buf.st_mode)) {
            if (s->perm && s->perm->has_error)
                return FALSE;
            else if (s->uid && s->uid->has_error)
                return FALSE;
            else if (s->gid && s->gid->has_error)
                return FALSE;
            else
                return TRUE;
        }
        else {
            return FALSE;
        }
        break;
    case TYPE_REMOTE :
        if (s->portlist) {
            Port_T port;
            int available = TRUE;
            for (port = s->portlist; port; port = port->next) {
                if (!port->is_available) {
                    available = FALSE;
                    break;
                }
            }
            return available;
        }
        else {
            return FALSE;
        }
        break;
    default :
        break;
    }
    return FALSE;
}

void start_httpd (int port, int backlog, char *bindAddr) {
    Socket_T S;
    stopped = Run.stopped;
    if ((myServerSocket = create_server_socket (port, backlog, bindAddr)) < 0) {
        log ("http server: Could not create a server socket at port %d -- %s\n", port, STRERROR);
        log ("monit HTTP server not available\n");
        if (Run.init) {
            sleep (1);
            kill_daemon (SIGTERM);
        }
    }
    else {
        initialize_service ();
        if (Run.httpdssl) {
            mySSLServerConnection = init_ssl_server (Run.httpsslpem, Run.httpsslclientpem);
            if (mySSLServerConnection == NULL) {
                log ("http server: Could not initilize SSL engine\n");
                log ("monit HTTP server not available\n");
                return;
            }
        }
        while (!stopped) {
            if (!(S = socket_producer (myServerSocket, port, mySSLServerConnection))) {
                continue;
            }
            http_processor (S);
        }
        if (S) {
            socket_free (& S);
        }
        delete_ssl_server_socket (mySSLServerConnection);
        close_socket (myServerSocket);
    }
}

void stop_httpd () {
    stopped = TRUE;
}

int add_host_allow (char *name) {
    struct hostent *hp;
    if (!(hp = gethostbyname (name))) {
        return FALSE;
    }
    else {
        HostsAllow h;
        NEW (h);
        while (*hp->h_addr_list) {
            h->name = xstrdup (inet_ntoa (*(structin_addr*) *hp->h_addr_list++));
            LOCK (hostlist_mutex)
            if (hostlist) {
                HostsAllow p, n;
                for (n = p = hostlist; p; n = p, p = p->next) {
                    if (!strcasecmp (p->name, name)) {
                        destroy_host_allow (h);
                        goto done;
                    }
                }
                n->next = h;
            }
            else {
                hostlist = h;
            }
        done :
            END_LOCK;
        }
    }
    return TRUE;
}

int has_hosts_allow () {
    int rv;
    LOCK (hostlist_mutex)
    rv = (hostlist != NULL);
    END_LOCK;
    return rv;
}

void destroy_hosts_allow () {
    if (has_hosts_allow ()) {
        LOCK (hostlist_mutex) destroy_host_allow (hostlist);
        hostlist = NULL;
        END_LOCK;
    }
}

static void initialize_service () {
    init_service ();
    check_Impl ();
}

static void check_Impl () {
    if ((Impl.doGet == 0) || (Impl.doPost == 0)) {
        log ("http server: Service Methods not implemented\n");
        _exit (1);
    }
}

static int authenticate (const char *host) {
    if (is_host_allow (host)) {
        return TRUE;
    }
    if (!has_hosts_allow () && (Run.credentials != NULL)) {
        return TRUE;
    }
    log ("%s: Denied connection from non-authorized client [%s]\n", prog, host);
    return FALSE;
}

static int is_host_allow (const char *name) {
    HostsAllow p;
    int rv = FALSE;
    LOCK (hostlist_mutex)
    for (p = hostlist; p; p = p->next) {
        if (!strncasecmp (p->name, name, STRLEN)) {
            rv = TRUE;
            break;
        }
    }
    END_LOCK;
    return rv;
}

static Socket_T socket_producer (int server, int port, void *sslserver) {
    int client;
    struct sockaddr_in in;
    int len = sizeof (struct sockaddr_in);
    if (can_read (server, 1)) {
        if ((client = accept (server, (struct sockaddr *) &in, &len)) < 0) {
            if (stopped) {
                log ("http server: service stopped\n");
            }
            else {
                log ("http server: cannot accept connection -- %s\n", STRERROR);
            }
            return NULL;
        }
    }
    else {
        return NULL;
    }
    if (set_noblock (client) < 0) {
        goto error;
    }
    if (!check_socket (client)) {
        goto error;
    }
    if (!authenticate (inet_ntoa (in.sin_addr))) {
        goto error;
    }
    return socket_create_a (client, inet_ntoa (in.sin_addr), port, sslserver);
error :
    close_socket (client);
    return NULL;
}

static void destroy_host_allow (HostsAllow p) {
    HostsAllow a = p;
    if (a->next) {
        destroy_host_allow (a -> next);
    }
    FREE (a -> name);
    FREE (a);
}

void *http_processor (void *s) {
    Socket_T S = s;
    if (!can_read (socket_get_socket (S), REQUEST_TIMEOUT)) {
        internal_error (S, SC_REQUEST_TIMEOUT, "Time out when handling the Request");
        goto shutdown;
    }
    do_service (S);
shutdown :
    socket_free (&S);
    return NULL;
}

void add_Impl (void *doGetFunc, void *doPostFunc) {
    Impl.doGet = doGetFunc;
    Impl.doPost = doPostFunc;
}

void send_error (HttpResponse res, int code, const char *msg) {
    char server [STRLEN];
    const char *err = get_status_string (code);
    reset_response (res);
    set_content_type (res, "text/html");
    set_status (res, code);
    out_print (res, "
%d %s" \ "
%s%s
" \ "%s" \ "\r\n", code, err, err, msg ? msg : "", SERVER_URL, get_server (server, STRLEN));
}

void send_redirect (HttpResponse res, const char *location) {
    reset_response (res);
    set_status (res, SC_MOVED_TEMPORARILY);
    set_header (res, "Location", location);
}

void out_print (HttpResponse res, const char *m, ... ) {
    if (m) {
        char *buf;
        va_list ap;
        long need = 0;
        ssize_t have = 0;
        va_start (ap, m);
        buf = format (m, ap, &need);
        va_end (ap);
        have = res->bufsize - res->bufused;
        if (have <= need) {
            res->outputbuffer = xresize (res->outputbuffer, (res->bufsize + need + RES_STRLEN));
            res->bufsize += (need + RES_STRLEN);
            if (!res->bufused) {
                memset (res -> outputbuffer, 0, res -> bufsize);
            }
        }
        memcpy (& res -> outputbuffer [res -> bufused], buf, need);
        res->bufused += need;
        res->outputbuffer[res->bufused] = 0;
        FREE (buf);
    }
}

void set_header (HttpResponse res, const char *name, const char *value) {
    HttpHeader h;
    ASSERT (res);
    ASSERT (name);
    NEW (h);
    h->name = xstrdup (name);
    h->value = xstrdup (value);
    if (res->headers) {
        HttpHeader n, p;
        for (n = p = res->headers; p; n = p, p = p->next) {
            if (!strcasecmp (p->name, name)) {
                FREE (p -> value);
                p->value = xstrdup (value);
                destroy_entry (h);
                return;
            }
        }
        n->next = h;
    }
    else {
        res->headers = h;
    }
}

void set_status (HttpResponse res, int code) {
    res->status = code;
    res->status_msg = get_status_string (code);
}

void set_content_type (HttpResponse res, const char *mime) {
    set_header (res, "Content-Type", mime);
}

const char *get_header (HttpRequest req, const char *name) {
    HttpHeader p;
    for (p = req->headers; p; p = p->next) {
        if (!strcasecmp (p->name, name)) {
            return (p->value);
        }
    }
    return NULL;
}

const char *get_parameter (HttpRequest req, const char *name) {
    HttpParameter p;
    for (p = req->params; p; p = p->next) {
        if (!strcasecmp (p->name, name)) {
            return (p->value);
        }
    }
    return NULL;
}

char *get_headers (HttpResponse res) {
    HttpHeader p;
    char buf [RES_STRLEN];
    char *b = buf;
    *buf = 0;
    for (p = res->headers; (((b - buf) + STRLEN) < RES_STRLEN) && p; p = p->next) {
        b += snprintf (b, STRLEN, "%s: %s\r\n", p->name, p->value);
    }
    return buf[0] ? xstrdup (buf) : NULL;
}

