        log ("LDAP: error sending data -- %s\n", STRERROR);
        return FALSE;
    }
    return TRUE;
}

int check_nntp (Socket_T s) {
    int status;
    char buf [STRLEN];
    char msg [STRLEN];
    ASSERT (s);
    if (socket_read (s, buf, sizeof (buf)) <= 0) {
        log ("NNTP: error receiving data -- %s\n", STRERROR);
        return FALSE;
    }
    chomp (buf, STRLEN);
    sscanf (buf, "%d %s", & status, msg);
    if (status != 200) {
        log ("NNTP error: %s\n", buf);
        return FALSE;
    }
    if (socket_print (s, "QUIT\r\n") < 0) {
        log ("NNTP: error sending data -- %s\n", STRERROR);
        return FALSE;
    }
    if (socket_read (s, buf, sizeof (buf)) <= 0) {
        log ("NNTP: error receiving data -- %s\n", STRERROR);
        return FALSE;
    }
    chomp (buf, STRLEN);
    sscanf (buf, "%d %s", & status, msg);
    if (status != 205) {
        log ("NNTP error: %s\n", buf);
        return FALSE;
    }
    return TRUE;
}

int check_pop (Socket_T s) {
