    colorDialog.ShowDialog ();
    if (colorDialog.DialogResult != DialogResult.Cancel) {
        conf.Editor_ForeColor = colorDialog.Color;
        conf.Editor_RecentColors = colorDialog.RecentColors;
        conf.Store ();
        surface.ForeColor = colorDialog.Color;
    }
}

